const quizData = [
    {
        question: 'data:image/jpeg;base64,',
        options: [
            'data:image/jpeg;base64,',
            'data:image/jpeg;base64,',
            'data:image/jpeg;base64,',
            'data:image/jpeg;base64,'
        ],
        answer: 'data:image/jpeg;base64,',
        solutionUrl: 'images/download.jpg'
      },
    {
      question: 'images/загруженное.jpg',
      options: [
        'images/2024-02-12_12-42-40.png',
        'images/2024-02-12_12-44-59.png',
        'images/2024-02-12_12-45-47.png',
        'images/2024-02-12_12-46-47.png'
      ],
      answer: 'images/2024-02-12_12-45-47.png',
      solutionUrl: 'images/2024-02-12_12-50-41.png'
    }
];
const quizContainer = document.getElementById('quiz'); 
  const resultContainer = document.getElementById('result'); 
  const submitButton = document.getElementById('submit'); 
  const retryButton = document.getElementById('retry'); 
  const showAnswerButton = document.getElementById('showAnswer'); 
   
  let currentQuestion = 0; 
  let score = 0; 
  let incorrectAnswers = []; 
   
  function shuffleArray(array) { 
    for (let i = array.length - 1; i > 0; i--) { 
      const j = Math.floor(Math.random() * (i + 1)); 
      [array[i], array[j]] = [array[j], array[i]]; 
    } 
  } 
function displayQuestion() {
    const questionData = quizData[currentQuestion];

    const questionContainer = document.createElement('div');
    questionContainer.className = 'question';

    const questionImage = document.createElement('img');
    questionImage.src = questionData.question;

    questionContainer.appendChild(questionImage);

    const optionsElement = document.createElement('div');
    optionsElement.className = 'options';

    for (let i = 0; i < questionData.options.length; i++) {
        const option = document.createElement('label');
        option.className = 'option';

        const radio = document.createElement('input');
        radio.type = 'radio';
        radio.name = 'quiz';
        radio.value = questionData.options[i];

        const optionContent = document.createElement('img');
        optionContent.src = questionData.options[i];

        option.appendChild(radio);
        option.appendChild(optionContent);
        optionsElement.appendChild(option);
    }

    quizContainer.innerHTML = '';
    quizContainer.appendChild(questionContainer);
    quizContainer.appendChild(optionsElement);
}
  
function checkAnswer(event) {
    event.preventDefault(); // Предотвращаем стандартное поведение кнопки submit

    const selectedOption = document.querySelector('input[name="quiz"]:checked');
    if (selectedOption) {
      const answer = selectedOption.value;
      if (answer === quizData[currentQuestion].answer) {
        score++;
      } else {
        incorrectAnswers.push({
          question: quizData[currentQuestion].question,
          incorrectAnswer: answer,
          correctAnswer: quizData[currentQuestion].answer,
        });
      }
      currentQuestion++;
      selectedOption.checked = false;
      if (currentQuestion < quizData.length) {
        displayQuestion();
      } else {
        displayResult();
      }
    }
  }

  
  function displayResult() {
    quizContainer.style.display = 'none';
    submitButton.style.display = 'none';
    retryButton.style.display = 'inline-block';
    showAnswerButton.style.display = 'inline-block';
    resultContainer.innerHTML = `You scored ${score} out of ${quizData.length}!`;
  }
  
  function retryQuiz() {
    currentQuestion = 0;
    score = 0;
    incorrectAnswers = [];
    quizContainer.style.display = 'block';
    submitButton.style.display = 'inline-block';
    retryButton.style.display = 'none';
    showAnswerButton.style.display = 'none';
    resultContainer.innerHTML = '';
    displayQuestion();
  }

  function showAnswer() {
    quizContainer.style.display = 'none';
    submitButton.style.display = 'none';
    retryButton.style.display = 'inline-block';
    showAnswerButton.style.display = 'none';

    let incorrectAnswersHtml = '';
    for (let i = 0; i < incorrectAnswers.length; i++) {
        incorrectAnswersHtml += `
            <p>
                <strong>Question:</strong> <img src="${incorrectAnswers[i].question}"><br>
                <strong>Your Answer:</strong> <img src="${incorrectAnswers[i].incorrectAnswer}"><br>
                <strong>Correct Answer:</strong> <img src="${incorrectAnswers[i].correctAnswer}"><br>
                <strong>Solution:</strong> <img src="${quizData[i].solutionUrl}">
            </p>
        `;
    }

    resultContainer.innerHTML = `
        <p>You scored ${score} out of ${quizData.length}!</p>
        <p>Incorrect Answers:</p>
        ${incorrectAnswersHtml}
    `;
}

  
  submitButton.addEventListener('click', checkAnswer);
  retryButton.addEventListener('click', retryQuiz);
  showAnswerButton.addEventListener('click', showAnswer);
  
  displayQuestion();
